mapper.select:
select distinct a.id AcountId,a.name Name,cr.Row_Id ,cr.Gt_Exp_Verf_Code,cr.Store_Id,cr.LAST_UPD newTime, ifnull(cr.REIM_STATUS,'新建') Reim_Status, ifnull(cr.APPRO_STATUS,'新建' ) Approval_Status,a2.name as Systems from (select appro_emp_pos_id,appro_emp_id,Reim_Id from CX_GT_APRO_DTL union select appro_emp_pos_id,appro_emp_id,Reim_Id from GK_COST_HISTORY_TEMP ) cad inner join gk_position p on cad.appro_emp_pos_id=p.intid inner join gk_employee e on cad.appro_emp_id=e.intId inner join cx_gt_reim cr on cad.Reim_Id = cr.row_Id inner join gk_account a on a.intId = cr.STORE_ID left join gk_account a1 on a.parentid=a1.id left join gk_account a2 on a1.parentid=a2.id left join gk_gt_reim_Temp tem on tem.reimId = cr.row_id where p.Id = #{positionId,jdbcType=VARCHAR} and cr.REIM_STATUS in ('异常','正常') and ( ( tem.ApplyPosId = p.intid and cr.APPRO_STATUS not like '%已驳回' ) )
and cr.APPRO_STATUS=#{approvalStatus}
and cr.Reim_Status=#{reimStatus}
and (a.name like concat('%', #{keyword,jdbcType=VARCHAR}, '%') or cr.Gt_Exp_Verf_Code like concat('%', #{keyword,jdbcType=VARCHAR}, '%') )
and a.name like concat('%', #{keyword,jdbcType=VARCHAR}, '%')
order by newTime desc

select WxType,get_lov_value('POSITION_TYPE',WXTYPE) WxName from gk_position where 1=1

    and Id= #{positionId,jdbcType=VARCHAR}


    and intId= #{intId,jdbcType=VARCHAR}
